import Orthographic from './orthographic';
import Perspective from './perspective';

export {
    Orthographic,
    Perspective,
};

import { vec3, mat4 } from 'gl-matrix';
import Object3 from '../core/object3';

class OrthographicCamera extends Object3 {
    constructor(options = {}) {
        super();

        Object.assign(this, {
            near: 0.1,
            far: 100,
        });

        Object.assign(this.position, {
            x: 0,
            y: 0,
            z: 99,
            ...options.position,
        });

        this.target = vec3.create();
        this.up = vec3.fromValues(0, 1, 0);
        this.projectionMatrix = mat4.create();
    }

    lookAt(v) {
        vec3.copy(this.target, v);
    }

    updateCameraMatrix() {
        mat4.ortho(
            this.projectionMatrix,
            -1.0,
            1.0,
            -1.0,
            1.0,
            this.near,
            this.far,
        );
    }
}

export default OrthographicCamera;

import { vec3, mat4 } from 'gl-matrix';
import Object3 from '../core/object3';

class PerspectiveCamera extends Object3 {
    constructor(options = {}) {
        super();

        Object.assign(this, {
            near: 1,
            far: 1000,
            fov: 35,
        });

        Object.assign(this.position, {
            x: 0,
            y: 100,
            z: 500,
            ...options.position,
        });

        this.target = vec3.create();
        this.up = vec3.fromValues(0, 1, 0);
        this.projectionMatrix = mat4.create();
    }

    lookAt(v) {
        vec3.copy(this.target, v);
    }

    updateCameraMatrix(width, height) {
        mat4.perspective(
            this.projectionMatrix,
            this.fov * (Math.PI / 180),
            width / height,
            this.near,
            this.far,
        );
    }
}

export default PerspectiveCamera;

export const DEFAULT_MATERIAL = 100;
export const BASIC_MATERIAL = 101;
export const FLATSHADING_MATERIAL = 102;
export const SEM_MATERIAL = 103;
export const RAW_MATERIAL = 104;

export const AMBIENT_LIGHT = 200;
export const DIRECTIONAL_LIGHT = 201;
export const POINT_LIGHT = 202;

export const MAX_DIRECTIONAL = 2;

import Orbit from './orbit';

export {
    Orbit,
};

import { vec3 } from 'gl-matrix';
import { clamp } from '../utils/math';
import { getContext } from '../session';

const offset = -Math.PI * 0.5;

class OrbitControls {
    constructor(camera) {
        const gl = getContext();

        this.camera = camera;
        this.domElement = gl.canvas;

        this.radius = Math.max(camera.position.x, camera.position.z);

        this.rx = Math.atan2(camera.position.y, this.radius);
        this.ry = Math.atan2(camera.position.z, camera.position.x) + offset;

        this.ox = 0;
        this.oy = 0;

        this.width = global.innerWidth;
        this.height = global.innerHeight;

        // rotation
        this.rotationSpeed = 5 * global.devicePixelRatio;

        // zoom
        this.zoomMin = 0.1;
        this.zoomMax = Infinity;
        this.zoomSpeed = 100;

        this.isDown = false;

        this.enable();
    }

    enable() {
        this.domElement.addEventListener('mousedown', this.onStart, false);
        this.domElement.addEventListener('mousemove', this.onMove, false);
        this.domElement.addEventListener('mouseup', this.onEnd, false);
        this.domElement.addEventListener('touchstart', this.onStart, false);
        this.domElement.addEventListener('touchmove', this.onMove, false);
        this.domElement.addEventListener('touchend', this.onEnd, false);
        global.addEventListener('wheel', this.onWheel, false);
    }

    disable() {
        this.domElement.removeEventListener('mousedown', this.onStart, false);
        this.domElement.removeEventListener('mousemove', this.onMove, false);
        this.domElement.removeEventListener('mouseup', this.onEnd, false);
        this.domElement.removeEventListener('touchstart', this.onStart, false);
        this.domElement.removeEventListener('touchmove', this.onMove, false);
        this.domElement.removeEventListener('touchend', this.onEnd, false);
        global.removeEventListener('wheel', this.onWheel, false);
    }

    onStart = (event) => {
        event.preventDefault();

        this.oy = this.ry;
        this.ox = this.rx;

        this.startY = event.pageX / this.width;
        this.startX = event.pageY / this.height;

        this.isDown = true;
    }

    onMove = (event) => {
        if (this.isDown) {
            const y = event.pageX / this.width;
            const x = event.pageY / this.height;
            this.rx = this.ox + (-(this.startX - x) * this.rotationSpeed);
            this.ry = this.oy + ((this.startY - y) * this.rotationSpeed);
            this.rx = clamp(this.rx, -Math.PI * 0.5, Math.PI * 0.5);
        }
    }

    onEnd = () => {
        this.isDown = false;
    }

    onWheel = (event) => {
        event.preventDefault();
        this.zoom(-event.deltaY);
    }

    zoom(delta) {
        this.radius += (delta / 1000) * this.zoomSpeed;
        this.radius = clamp(this.radius, this.zoomMin, this.zoomMax);
    }

    update() {
        const y = this.radius * Math.sin(this.rx);
        const r = this.radius * Math.cos(this.rx);
        const x = Math.sin(this.ry) * r;
        const z = Math.cos(this.ry) * r;
        vec3.set(this.camera.position.data, x, y, z);
    }
}

export default OrbitControls;

import Vao from '../renderer/helpers/vao';
import { createProgram } from '../renderer/helpers/program';
import { getContext, GL_TRIANGLES } from '../session';

const programs = {};

class Material {
    constructor() {
        this.defines = {};
        this.attributes = Object.assign({
            a_position: {
                type: 'vec3',
                value: null,
            },
            a_normal: {
                type: 'vec3',
                value: null,
            },
            a_uv: {
                type: 'vec2',
                value: null,
            },
        });
        this.uniforms = {};

        this.vertex = '';
        this.fragment = '';

        this.vao = new Vao();
        this.program = null;
        this.indices = null;

        this.glMode = GL_TRIANGLES;
    }

    createProgram() {
        const gl = getContext();
        const key = `shader-${this.type}`;
        if (programs[key] === undefined) {
            const program = createProgram(gl, this.vertex, this.fragment);
            programs[key] = program;
        }
        this.program = programs[key];
    }

    initAttributes() {
        const gl = getContext();
        for (const prop in this.attributes) {
            const current = this.attributes[prop];
            const location = gl.getAttribLocation(this.program, prop);
            const buffer = gl.createBuffer();
            gl.bindBuffer(gl.ARRAY_BUFFER, buffer);
            gl.bufferData(gl.ARRAY_BUFFER, current.value, gl.DYNAMIC_DRAW); // STATIC_DRAW

            let size;
            switch (current.type) {
            case 'vec3':
                size = 3;
                break;
            case 'vec2':
                size = 2;
                break;
            default:
                size = 1;
            }

            Object.assign(current, {
                location,
                buffer,
                size,
            });
        }

        if (this.indices) {
            this.indexBuffer = gl.createBuffer();
        }
    }

    initUniforms() {
        const gl = getContext();

        const textureIndices = [
            gl.TEXTURE0,
            gl.TEXTURE1,
            gl.TEXTURE2,
            gl.TEXTURE3,
            gl.TEXTURE4,
            gl.TEXTURE5,
        ];
        Object.keys(this.uniforms).forEach((prop, i) => {
            const current = this.uniforms[prop];
            const location = gl.getUniformLocation(this.program, prop);

            if (current.type === 'sampler2D') {
                current.textureIndex = i;
                current.activeTexture = textureIndices[i];
            }

            current.location = location;
        });
    }

    init() {
        this.createProgram();
        this.initAttributes();
        this.initUniforms();

        this.vao.bind();

        this.bind();

        this.vao.unbind();
        this.unbind();
    }

    destroy() {
        // destroy buffers
    }

    bind() {
        const gl = getContext();
        Object.keys(this.attributes).forEach((key) => {
            const { location, buffer, size } = this.attributes[key];
            if (location !== -1) {
                gl.enableVertexAttribArray(location);
                gl.bindBuffer(gl.ARRAY_BUFFER, buffer);
                gl.vertexAttribPointer(location, size, gl.FLOAT, false, 0, 0);
                gl.bindBuffer(gl.ARRAY_BUFFER, null);
            }
        });

        if (this.indices) {
            gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, this.indexBuffer);
            gl.bufferData(gl.ELEMENT_ARRAY_BUFFER, this.indices, gl.STATIC_DRAW);
        }
    }

    unbind() {
        const gl = getContext();
        gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, null);
    }

    update() {
        // update uniforms
        const gl = getContext();
        Object.keys(this.uniforms).forEach((key) => {
            const uniform = this.uniforms[key];

            switch (uniform.type) {
            case 'mat4':
                gl.uniformMatrix4fv(uniform.location, false, uniform.value);
                break;
            case 'mat3':
                gl.uniformMatrix3fv(uniform.location, false, uniform.value);
                break;
            case 'vec4':
                gl.uniform4fv(uniform.location, uniform.value);
                break;
            case 'vec3':
                gl.uniform3fv(uniform.location, uniform.value);
                break;
            case 'vec2':
                gl.uniform2fv(uniform.location, uniform.value);
                break;
            case 'float':
                gl.uniform1f(uniform.location, uniform.value);
                break;
            case 'sampler2D':
                gl.uniform1i(uniform.location, uniform.textureIndex);
                gl.activeTexture(uniform.activeTexture);
                gl.bindTexture(gl.TEXTURE_2D, uniform.value);
                break;
            default:
                console.warn('unknown uniform type', uniform.type);
            }
        });
    }
}

export default Material;

import Object3 from './object3';
import { getContext } from '../session';

class Model extends Object3 {
    constructor(geometry, material) {
        super();

        this.geometry = {
            positions: new Float32Array(geometry.positions),
            indices: new Uint16Array(geometry.indices),
            normals: new Float32Array(geometry.normals || geometry.positions.length),
            uvs: new Float32Array(geometry.uvs || geometry.positions.length / 1.5),
        };

        this.material = material;

        // assign attributes and indices
        this.material.attributes.a_position.value = this.geometry.positions;
        this.material.attributes.a_normal.value = this.geometry.normals;
        this.material.attributes.a_uv.value = this.geometry.uvs;
        this.material.indices = this.geometry.indices;

        this.positionsNeedUpdate = false;
    }

    init() {
        this.material.init();
    }

    bind() {
        this.material.bind();
    }

    update() {
        this.material.update();

        if (this.positionsNeedUpdate === true) {
            const gl = getContext();
            gl.enableVertexAttribArray(this.material.attributes.a_position.location);
            gl.bindBuffer(gl.ARRAY_BUFFER, this.material.attributes.a_position.buffer);
            gl.bufferData(gl.ARRAY_BUFFER, this.geometry.positions, gl.DYNAMIC_DRAW);

            // do I need to bind this?
            // if (this.indices) {
            //     gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, this.indexBuffer);
            //     gl.bufferData(gl.ELEMENT_ARRAY_BUFFER, this.indices, gl.STATIC_DRAW);
            // }
        }
        this.draw();
    }

    unbind() {
        this.material.unbind();
    }

    draw() {
        const gl = getContext();

        if (this.polygonOffset) {
            gl.enable(gl.POLYGON_OFFSET_FILL);
            gl.polygonOffset(this.polygonOffsetFactor, this.polygonOffsetUnits);
        } else {
            gl.disable(gl.POLYGON_OFFSET_FILL);
        }

        if (this.geometry.indices.length > 0) {
            gl.drawElements(
                this.material.glMode(),
                this.geometry.indices.length,
                gl.UNSIGNED_SHORT,
                0,
            );
        } else {
            gl.drawArrays(
                this.material.glMode(),
                0,
                this.geometry.positions.length / 3,
            );
        }
    }

    destroy() {
        this.material.destroy();
    }
}

export default Model;

import { vec3, mat4, quat } from 'gl-matrix';

import Vector3 from './vector3';

let uuid = 0;
let axisAngle = 0;
const quaternionAxisAngle = vec3.create();

class Object3 {
    constructor() {
        this.uid = uuid++;

        this.position = new Vector3();
        this.rotation = new Vector3();
        this.scale = new Vector3(1, 1, 1);
        this.quaternion = quat.create();
        this.target = vec3.create();
        this.up = vec3.fromValues(0, 1, 0);

        this.parent = null;
        this.children = [];

        this.parentMatrix = mat4.create();
        this.modelMatrix = mat4.create();
        this.lookAtMatrix = mat4.create();

        // use mat4.targetTo rotation when set to true
        this.lookToTarget = false; // use lookAt rotation when set to true

        // enable polygonOffset (z-fighting)
        this.polygonOffset = false;
        this.polygonOffsetFactor = 0;
        this.polygonOffsetUnits = 1;
    }

    addModel(model) {
        model.parent = this; // eslint-disable-line
        this.children.push(model);
    }

    removeModel(model) {
        const index = this.children.indexOf(model);
        if (index !== -1) {
            model.destroy();
            this.children.splice(index, 1);
        }
    }

    updateMatrices() {
        mat4.identity(this.parentMatrix);
        mat4.identity(this.modelMatrix);
        mat4.identity(this.lookAtMatrix);
        quat.identity(this.quaternion);

        if (this.parent) {
            mat4.copy(this.parentMatrix, this.parent.modelMatrix);
            mat4.multiply(this.modelMatrix, this.modelMatrix, this.parentMatrix);
        }

        if (this.lookToTarget) {
            mat4.targetTo(this.lookAtMatrix, this.position.data, this.target, this.up);
            mat4.multiply(this.modelMatrix, this.modelMatrix, this.lookAtMatrix);
        } else {
            mat4.translate(this.modelMatrix, this.modelMatrix, this.position.data);
            quat.rotateX(this.quaternion, this.quaternion, this.rotation.x);
            quat.rotateY(this.quaternion, this.quaternion, this.rotation.y);
            quat.rotateZ(this.quaternion, this.quaternion, this.rotation.z);
            axisAngle = quat.getAxisAngle(quaternionAxisAngle, this.quaternion);
            mat4.rotate(this.modelMatrix, this.modelMatrix, axisAngle, quaternionAxisAngle);
        }
        mat4.scale(this.modelMatrix, this.modelMatrix, this.scale.data);
    }
}

export default Object3;

import Object3 from './object3';
import {
    AMBIENT_LIGHT,
    DIRECTIONAL_LIGHT,
    POINT_LIGHT,
} from '../constants';

class Scene extends Object3 {
    constructor() {
        super();

        this.ambient = [];
        this.directional = [];
        this.point = [];
    }

    traverse(object) {
        if (object === undefined) {
            object = this; // eslint-disable-line
        }

        for (let i = 0; i < object.children.length; i++) {
            this.traverse(object.children[i]);
        }

        if (object.parent === null) {
            return;
        }

        object.updateMatrices();
    }

    addLight(light) {
        switch (light.type) {
        case AMBIENT_LIGHT:
            this.ambient.push(light);
            break;
        case DIRECTIONAL_LIGHT:
            this.directional.push(light);
            break;
        case POINT_LIGHT:
            this.point.push(light);
            break;
        default:
            // nothing
        }
    }

    removeLight(light) {
        let index = this.ambient.indexOf(light);
        if (index !== -1) {
            light.destroy();
            this.ambient.splice(index, 1);
        }

        // directional
        index = this.directional.indexOf(light);
        if (index !== -1) {
            light.destroy();
            this.directional.splice(index, 1);
        }

        // point
        index = this.point.indexOf(light);
        if (index !== -1) {
            light.destroy();
            this.point.splice(index, 1);
        }
    }
}

export default Scene;

import { getContext } from '../session';

class Texture {
    constructor(props = {}) {
        const gl = getContext();

        Object.assign(this, {
            magFilter: gl.NEAREST,
            minFilter: gl.NEAREST,
            wrapS: gl.CLAMP_TO_EDGE,
            wrapT: gl.CLAMP_TO_EDGE,
        }, props);

        const data = new Uint8Array([0, 0, 0, 255]);
        this.texture = gl.createTexture();
        gl.bindTexture(gl.TEXTURE_2D, this.texture);
        gl.texImage2D(gl.TEXTURE_2D, 0, gl.RGBA, 1, 1, 0, gl.RGBA, gl.UNSIGNED_BYTE, data);
        gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, this.magFilter);
        gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, this.minFilter);
        gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, this.wrapS);
        gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, this.wrapT);

        gl.bindTexture(gl.TEXTURE_2D, null);
    }

    fromImage(url) {
        const img = new Image();
        img.crossOrigin = '';
        img.onload = () => {
            this.update(img);
        };
        img.src = url;
    }

    update(image) {
        const gl = getContext();
        gl.bindTexture(gl.TEXTURE_2D, this.texture);
        gl.generateMipmap(gl.TEXTURE_2D);
        gl.pixelStorei(gl.UNPACK_FLIP_Y_WEBGL, true);
        gl.texImage2D(gl.TEXTURE_2D, 0, gl.RGBA, gl.RGBA, gl.UNSIGNED_BYTE, image);
        gl.bindTexture(gl.TEXTURE_2D, null);
    }
}

export default Texture;

import { vec3 } from 'gl-matrix';

class Vector3 {
    constructor(x = 0, y = 0, z = 0) {
        this.data = vec3.fromValues(x, y, z);
    }

    set(x, y, z) {
        this.x = x;
        this.y = y;
        this.z = z;
    }

    set x(value) {
        vec3.set(this.data, value, this.data[1], this.data[2]);
    }

    get x() {
        return this.data[0];
    }

    set y(value) {
        vec3.set(this.data, this.data[0], value, this.data[2]);
    }

    get y() {
        return this.data[1];
    }

    set z(value) {
        vec3.set(this.data, this.data[0], this.data[1], value);
    }

    get z() {
        return this.data[2];
    }
}

export default Vector3;

import { generateVertexNormals, flatten, unflatten } from '../utils/geometry';

class Icosahedron {
    constructor(radius, detail = 0) {
        const t = 0.5 + (Math.sqrt(5) / 2);
        let positions = [
            -1, +t, 0,
            +1, +t, 0,
            -1, -t, 0,
            +1, -t, 0,
            0, -1, +t,
            0, +1, +t,
            0, -1, -t,
            0, +1, -t,
            +t, 0, -1,
            +t, 0, +1,
            -t, 0, -1,
            -t, 0, +1,
        ];
        const faces = [
            0, 11, 5,
            0, 5, 1,
            0, 1, 7,
            0, 7, 10,
            0, 10, 11,
            1, 5, 9,
            5, 11, 4,
            11, 10, 2,
            10, 7, 6,
            7, 1, 8,
            3, 9, 4,
            3, 4, 2,
            3, 2, 6,
            3, 6, 8,
            3, 8, 9,
            4, 9, 5,
            2, 4, 11,
            6, 2, 10,
            8, 6, 7,
            9, 8, 1,
        ];

        const uvs = [];

        let complex = {
            faces: unflatten(faces, 3),
            positions: unflatten(positions, 3),
        };

        let d = Math.min(detail, 3);

        while (d-- > 0) {
            complex = this.subdivide(complex);
        }

        // generate uvs
        for (let i = 0; i < complex.positions.length; i++) {
            const position = this.normalize(complex.positions[i]);
            const u = 0.5 * (-(Math.atan2(position[2], -position[0]) / Math.PI) + 1.0);
            const v = 0.5 + (Math.asin(position[1]) / Math.PI);
            uvs.push([1 - u, 1 - v]);
        }

        // http://mft-dev.dk/uv-mapping-sphere/
        // this.fixPoleUVs(complex.positions, complex.faces, uvs);

        // scale positions
        positions = complex.positions; // eslint-disable-line
        for (let i = 0; i < positions.length; i++) {
            // this.normalize(positions[i]);
            this.scale(positions[i], radius);
        }

        const geometry = {
            positions: flatten(complex.positions),
            indices: flatten(complex.faces),
            normals: null,
            uvs: flatten(uvs, 2),
        };

        geometry.normals = generateVertexNormals(geometry.positions, geometry.indices);

        return geometry;
    }

    fixPoleUVs(positions, cells, uvs) {
        const northIndex = this.firstYIndex(positions, 1);
        const southIndex = this.firstYIndex(positions, -1);

        if (northIndex === -1 || southIndex === -1) {
            // could not find any poles, bail early
            return;
        }

        const newVertices = positions.slice();
        const newUvs = uvs.slice();
        let verticeIndex = newVertices.length - 1;

        function visit(cell, poleIndex, b, c) {
            const uv1 = uvs[b];
            const uv2 = uvs[c];
            uvs[poleIndex][0] = (uv1[0] + uv2[0]) / 2;
            verticeIndex++;
            newVertices.push(positions[poleIndex].slice());
            newUvs.push(uvs[poleIndex].slice());
            cell[0] = verticeIndex;
        }

        for (let i = 0; i < cells.length; i++) {
            const cell = cells[i];
            const a = cell[0];
            const b = cell[1];
            const c = cell[2];

            if (a === northIndex) {
                visit(cell, northIndex, b, c);
            } else if (a === southIndex) {
                visit(cell, southIndex, b, c);
            }
        }

        positions = newVertices;
        uvs = newUvs;
    }

    firstYIndex(list, value) {
        for (let i = 0; i < list.length; i++) {
            const vec = list[i];
            if (Math.abs(vec[1] - value) <= 1e-4) {
                return i;
            }
        }
        return -1;
    }

    normalize(vec) {
        let mag = 0;
        for (let n = 0; n < vec.length; n++) {
            mag += vec[n] * vec[n];
        }
        mag = Math.sqrt(mag);

        // avoid dividing by zero
        if (mag === 0) {
            return Array.apply(null, new Array(vec.length)).map(Number.prototype.valueOf, 0); // eslint-disable-line
        }

        for (let n = 0; n < vec.length; n++) {
            vec[n] /= mag;
        }

        return vec;
    }

    scale(vec, factor) {
        for (let n = 0; n < vec.length; n++) {
            vec[n] *= factor;
        }
        return vec;
    }

    subdivide(complex) {
        const { positions, faces } = complex;

        const newCells = [];
        const newPositions = [];
        const midpoints = {};
        let l = 0;

        function midpoint(a, b) {
            return [
                (a[0] + b[0]) / 2, (a[1] + b[1]) / 2, (a[2] + b[2]) / 2,
            ];
        }

        function pointToKey(point) {
            return `${point[0].toPrecision(6)},${point[1].toPrecision(6)},${point[2].toPrecision(6)}`;
        }

        function getMidpoint(a, b) {
            const point = midpoint(a, b);
            const pointKey = pointToKey(point);
            const cachedPoint = midpoints[pointKey];
            if (cachedPoint) {
                return cachedPoint;
            }
            midpoints[pointKey] = point;
            return midpoints[pointKey];
        }

        for (let i = 0; i < faces.length; i++) {
            const cell = faces[i];
            const c0 = cell[0];
            const c1 = cell[1];
            const c2 = cell[2];
            const v0 = positions[c0];
            const v1 = positions[c1];
            const v2 = positions[c2];

            const a = getMidpoint(v0, v1);
            const b = getMidpoint(v1, v2);
            const c = getMidpoint(v2, v0);

            let ai = newPositions.indexOf(a);
            if (ai === -1) {
                ai = l++;
                newPositions.push(a);
            }
            let bi = newPositions.indexOf(b);
            if (bi === -1) {
                bi = l++;
                newPositions.push(b);
            }
            let ci = newPositions.indexOf(c);
            if (ci === -1) {
                ci = l++;
                newPositions.push(c);
            }

            let v0i = newPositions.indexOf(v0);
            if (v0i === -1) {
                v0i = l++;
                newPositions.push(v0);
            }
            let v1i = newPositions.indexOf(v1);
            if (v1i === -1) {
                v1i = l++;
                newPositions.push(v1);
            }
            let v2i = newPositions.indexOf(v2);
            if (v2i === -1) {
                v2i = l++;
                newPositions.push(v2);
            }

            newCells.push([v0i, ai, ci]);
            newCells.push([v1i, bi, ai]);
            newCells.push([v2i, ci, bi]);
            newCells.push([ai, bi, ci]);
        }

        return {
            faces: newCells,
            positions: newPositions,
        };
    }
}

